mapper.insert:
INSERT INTO course_student
(course_id, select_user_id

    , create_date



    , is_pay


    , pay_num


    , comment

) VALUES
    ( #{courseId}, #{selectUserId}


    , #{createDate}



    , #{isPay}


    , #{payNum}


    , #{comment}

)

mapper.select:
SELECT w.id id, w.course_id courseId, w.select_user_id selectUserId, date_format(w.create_date, '%Y.%m.%d %T') createDate, date_format(w.update_date, '%Y.%m.%d %T') updateDate,
u.content content, u.brief, u.capacity, u.course_date courseDate, u.tuition, s.nickname, u.grade, u.tuition_type tuitionType, u.final_tuition finalTuition FROM course_student w LEFT JOIN course_teacher u ON w.course_id = u.id LEFT JOIN sys_user s ON u.create_user = s.id WHERE w.delete_status = '1'
AND w.select_user_id = #{selectUserId}
ORDER BY w.id DESC LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE course_teacher
   SET id = id

    , content = #{content}


    , brief = #{brief}


    , capacity = #{capacity}


    , course_date = #{courseDate}


    , tuition = #{tuition}


    , create_user = #{createUser}


    , update_user = #{updateUser}


    , delete_status = #{deleteStatus}


    , teacher_type = #{teacherType}


    , grade = #{grade}

WHERE id = #{id}
