mapper.insert:
INSERT INTO course_student
(course_id, select_user_id

    , create_date



    , is_pay


    , pay_num


    , comment


    , course_date

) VALUES
    ( #{courseId}, #{selectUserId}


    , #{createDate}



    , #{isPay}


    , #{payNum}


    , #{comment}


    , #{courseDate}

)

mapper.select:
SELECT count(0)
FROM course_student w
WHERE w.delete_status = '1'

    AND w.select_user_id = #{selectUserId}


    AND w.course_id = #{courseId}

SELECT w.id id, w.course_id courseId, w.select_user_id selectUserId, date_format(w.create_date, '%Y.%m.%d %T') createDate, date_format(w.update_date, '%Y.%m.%d %T') updateDate,
u.content content, u.brief, u.capacity, w.course_date courseDateStudent, u.tuition, s.nickname, u.grade, u.tuition_type tuitionType, u.final_tuition finalTuition, sys_attach.origin_fileName originFileName, sys_attach.id attachId, sys_attach2.origin_fileName originFileNameStu, sys_attach2.id attachIdStu FROM course_student w LEFT JOIN course_teacher u ON w.course_id = u.id LEFT JOIN sys_user s ON u.create_user = s.id left JOIN sys_attach on u.id= sys_attach.business_id and sys_attach.type='course-teacher' left JOIN sys_attach sys_attach2 on w.id= sys_attach2.business_id and sys_attach2.type='course-student' WHERE w.delete_status = '1'
AND w.select_user_id = #{selectUserId}
AND w.course_id = #{courseId}
ORDER BY w.id DESC LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE course_student
   SET id = id

    , comment = #{comment}


    , delete_status = #{deleteStatus}


    , is_pay = #{isPay}


    , course_date = #{courseDate}


    , finance_is_pay = #{financeIsPay}

WHERE id = #{id}
