mapper.insert:
INSERT INTO course_teacher
(content

    , brief


    , capacity


    , course_date


    , create_user


    , update_user


    , start_date


    , end_date


    , teacher_type


    , grade


    , tuition_type


    , tuition


    , tuition_sub_type


    , final_tuition

) VALUES
    ( #{content}

    , #{brief}


    , #{capacity}


    , #{courseDate}


    , #{createUser}


    , #{updateUser}


    , #{startDate}


    , #{endDate}


    , #{teacherType}


    , #{grade}


    , #{tuitionType}


    , #{tuition}


    , #{tuitionSubType}


    , #{finalTuition}

)

mapper.select:
SELECT count(0)
FROM course_teacher w
WHERE w.delete_status = '1'

    AND w.content like concat(concat('%',#{content}),'%')


    AND w.create_user = #{author}


    and w.id not in (select course_id from course_student where select_user_id = #{studentCanPick} and delete_status = '1')


    AND w.id  <> #{idNotEqual}


    AND (

        FIND_IN_SET(#{item},course_date)

    )

SELECT w.id id, w.content content, date_format(w.create_time, '%Y.%m.%d %T') createTime, date_format(w.update_time, '%Y.%m.%d %T') updateTime, u.nickname, w.brief, w.capacity, w.course_date courseDate, w.tuition, w.create_user, w.update_user, w.grade, w.tuition_type tuitionType, w.tuition_sub_type tuitionSubType, w.final_tuition finalTuition, w.teacher_type teacherType FROM course_teacher w LEFT JOIN sys_user u ON w.create_user = u.id WHERE w.delete_status = '1'
AND w.content like concat(concat('%',#{content}),'%')
AND w.course_date like concat(concat('%',#{courseDate}),'%')
AND w.create_user = #{author}
and w.id not in (select course_id from course_student where select_user_id = #{studentCanPick} and delete_status = '1')
LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE course_teacher
   SET id = id

    , content = #{content}


    , brief = #{brief}


    , capacity = #{capacity}


    , course_date = #{courseDate}


    , create_user = #{createUser}


    , update_user = #{updateUser}


    , delete_status = #{deleteStatus}


    , teacher_type = #{teacherType}


    , grade = #{grade}



    , tuition_type = #{tuitionType}


    , tuition = #{tuition}


    , tuition_sub_type = #{tuitionSubType}


    , final_tuition = #{finalTuition}


WHERE id = #{id}
