mapper.insert:
INSERT INTO course_teacher
(content

    , brief


    , capacity


    , course_date


    , create_user


    , update_user


    , start_date


    , end_date


    , teacher_type


    , grade


    , tuition_type


    , tuition


    , tuition_sub_type


    , final_tuition


    , teacher_name

) VALUES
    ( #{content}

    , #{brief}


    , #{capacity}


    , #{courseDate}


    , #{createUser}


    , #{updateUser}


    , #{startDate}


    , #{endDate}


    , #{teacherType}


    , #{grade}


    , #{tuitionType}


    , #{tuition}


    , #{tuitionSubType}


    , #{finalTuition}


    , #{teacherName}

)

mapper.select:
SELECT count(0) FROM course_teacher w WHERE w.delete_status = '1'
AND w.content like concat(concat('%',#{content}),'%')
AND w.teacher_name = #{author}
and w.status = 'publish' and w.id not in (select course_id from course_student where select_user_id = #{studentCanPick} and delete_status = '1')
AND w.id <> #{idNotEqual}
AND (
FIND_IN_SET(#{item},course_date)
)
AND w.grade like concat(concat('%',#{studentGradeFilter}),'%')

mapper.update:
UPDATE course_teacher
   SET id = id

    , content = #{content}


    , brief = #{brief}


    , capacity = #{capacity}


    , course_date = #{courseDate}


    , create_user = #{createUser}


    , update_user = #{updateUser}


    , delete_status = #{deleteStatus}


    , teacher_type = #{teacherType}


    , grade = #{grade}



    , tuition_type = #{tuitionType}


    , tuition = #{tuition}


    , tuition_sub_type = #{tuitionSubType}


    , final_tuition = #{finalTuition}


    , status = #{status}


    , teacher_name = #{teacherName}



    , need_training_aid = #{needTrainingAid}


    , phone = #{phone}


    , price = #{price}


    , recommend_brand = #{recommendBrand}

WHERE id = #{id}
